mapper.select:
select id, userName, password
from player
where id = #{id}

select id, userName, password
from player
where userName = #{userName}

select count(1)
from player
where userName = #{userName}
  and password = #{password}

select count(1)
from player
where userName = #{userName}

mapper.update:
update player
set activeUUID = #{activeUUID}
where id = #{id}
